import React, { useState, useEffect } from "react";
import { Grid } from "@mui/material";

import Login from "./components/Login/Login";
import Home from "./components/Home/Home";
import Navigation from "./components/MainHeader/Navigation";


function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  useEffect(() => {
    const storedUsewrINfo = localStorage.getItem("IsLoggedIn");
    if (storedUsewrINfo === "1") {
      setIsLoggedIn(true);
    }
  }, []);

  const user={
    Id:'abcd.com',
    password:'12345678900',
  }

  const loginHandler = (email, password) => {
    if(email===user.Id && password===user.password){
      setIsLoggedIn(true);
      localStorage.setItem('IsLoggedIn','1')

    }

  };

  const logoutHandler = () => {
    setIsLoggedIn(false);
  };

  return (
    <Grid>

      {!isLoggedIn && <Login onLogin={loginHandler} />}
      {isLoggedIn && <Home />}
      <Navigation isLoggedIn={isLoggedIn} onLogout={logoutHandler} />
    </Grid>
  );
}

export default App;
